import * as React from 'react';
import Card from '../Card';
import TextContainer from '../TextContainer';
import ButtonGroup from '../ButtonGroup';
import { buttonFrom } from '../Button';
import Heading from '../Heading';
import Image from '../Image';
import * as styles from './CalloutCard.scss';
export default function CalloutCard({ title, children, illustration, primaryAction, secondaryAction, }) {
    const primaryActionMarkup = buttonFrom(primaryAction);
    const secondaryActionMarkup = secondaryAction
        ? buttonFrom(secondaryAction, { plain: true })
        : null;
    const buttonMarkup = secondaryActionMarkup ? (<ButtonGroup>
      {primaryActionMarkup}
      {secondaryActionMarkup}
    </ButtonGroup>) : (primaryActionMarkup);
    return (<Card sectioned>
      <div className={styles.CalloutCard}>
        <div className={styles.Content}>
          <div className={styles.Title}>
            <Heading>{title}</Heading>
          </div>
          <TextContainer>{children}</TextContainer>
          <div className={styles.Buttons}>{buttonMarkup}</div>
        </div>

        <Image alt="" className={styles.Image} source={illustration}/>
      </div>
    </Card>);
}
